// src/components/Dashboard.jsx
import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import axios from "axios";
import { Chart, registerables } from "chart.js";
import carddet from "./carddata";

Chart.register(...registerables);

const Dashboard = () => {
  const [patientData, setPatientData] = useState(null);

  useEffect(() => {
    axios
      .get("https://api.example.com/jessica-taylor")
      .then((response) => {
        setPatientData(response.data);
      })
      .catch((error) => {
        console.error("There was an error fetching the patient data!", error);
      });
  }, []);

  const data = {
    labels: [
      "Oct 2023",
      "Nov 2023",
      "Dec 2023",
      "Jan 2024",
      "Feb 2024",
      "Mar 2024",
    ],
    datasets: [
      {
        label: "Systolic",
        data: [120, 125, 130, 135, 140, 145],
        borderColor: "#FF6384",
        fill: false,
      },
      {
        label: "Diastolic",
        data: [80, 82, 84, 86, 88, 90],
        borderColor: "#36A2EB",
        fill: false,
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      tooltip: {
        callbacks: {
          label: function (tooltipItem) {
            return tooltipItem.dataset.label + ": " + tooltipItem.raw;
          },
        },
      },
    },
    scales: {
      y: {
        beginAtZero: false,
      },
    },
  };

  return (
    <div className="mt-[10px] ml-[400px] left-[18px] w-[766px] h-[670px] opacity-100 ">
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-bold mb-4">Diagnosis History</h2>
        <Line data={data} options={options} />
      </div>
      {patientData && (
        <div className="mt-8">
          <h3 className="text-xl font-bold mb-4">Patient Details</h3>
          <p>Name: {patientData.name}</p>
          <p>Date of Birth: {patientData.date_of_birth}</p>
          <p>Gender: {patientData.gender}</p>
          {/* Add more patient details as needed */}
        </div>
      )}
      {carddet.map((details, index) => (
  <div key={index} className="flex  flex-cols-3 space-y-4">
    {/* First Inner Div */}
    <div className="bg-[#E0F3FA] rounded-[12px] p-4 w-[228px] h-[242px] flex flex-col items-center justify-center">
      <img
        src={details.iconname}
        alt="Icon 1"
        className="rounded-full w-[80px] h-[80px]"
      />
      <p className="text-center text-[16px] font-bold mt-2">
        {details.name}
      </p>
      <p className="text-center text-[14px] font-semibold text-gray-600">
        {details.value}
      </p>
      <p className="text-center text-[12px] text-gray-500">
        {details.subname}
      </p>
    </div>
  </div>
))}

    </div>
  );
};

export default Dashboard;
